import React from 'react';
import './Footer.css';
import * as motion from "motion/react-client"

export function Gestures() {
    return (
        <motion.div
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.8 }}
            style={box}
        />
    )
}

const box = {
    width: 100,
    height: 100,
    backgroundColor: "#9911ff",
    borderRadius: 5,
}

function Footer() {
  return (
    <footer id="footer">
      <p>&copy; 2025 Rowan Morrison</p>
      <li className="circle-container">
									<div id="mode-toggle">
									<motion.span
                                    className="mode-toggle-button"
                                    id="light-icon"
                                    whileHover={{ scale: 1.2 }}
                                    whileTap={{ scale: 0.8 }}
                                    style={box}
                                    />
								</div>
							</li>
    </footer>
  );
}

export default Footer;

const systemSettingDark = window.matchMedia("(prefers-color-scheme: dark)");

function toggleDarkMode(isDarkMode) {
    if (isDarkMode) {
        document.body.classList.add("dark-mode");
        localStorage.setItem("dark-mode", "enabled");
    } else {
        document.body.classList.remove("dark-mode");
        localStorage.setItem("dark-mode", "disabled");
    }
    setLogoBasedOnMode();
}

function setLogoBasedOnMode() {
    const myLogo = document.getElementById("rowan-logo");
    if (!myLogo) return;

    if (document.body.classList.contains('dark-mode')) {
        myLogo.src = '/Rowan-Morrison-logo-01.png'; 
    } else {
        myLogo.src = '/Rowan-Morrison-logo-02.png'; 
    }
}


systemSettingDark.addEventListener("change", (event) => {
    toggleDarkMode(event.matches);
});

document.addEventListener('DOMContentLoaded', function () {
    const modeToggle = document.getElementById("mode-toggle");

    toggleDarkMode(systemSettingDark.matches || localStorage.getItem("dark-mode") === "enabled");

    if (modeToggle) {
        modeToggle.addEventListener('click', function (e) {
            e.preventDefault();
            const isDarkMode = !document.body.classList.contains('dark-mode');
            toggleDarkMode(isDarkMode);
        });
    }
});
