import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './WorkDetail.css';

const workDetails = {
  'downtown-ring': {
    title: 'Downtown Ring',
    description: 'A ring inspired by downtown architecture.',
    image: '/images/downtown.jpg',
  },
  'silk-study': {
    title: 'Silk Study',
    description: 'Experimental textile design using silk.',
    image: '/images/silk.jpg',
  },
};

const WorkDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const work = workDetails[id];

  if (!work) return <div>Work not found.</div>;

  return (
    <div className="work-detail">
      <button className="close-btn" onClick={() => navigate(-1)}>Ã—</button>
      <div className="work-image">
        <img src={work.image} alt={work.title} />
      </div>
      <div className="work-info">
        <h1>{work.title}</h1>
        <p>{work.description}</p>
      </div>
    </div>
  );
};

export default WorkDetail;
