import React, { useEffect, useRef } from 'react';
import './Footer.css';
import { motion } from "framer-motion";

function Footer() {
  const toggleRef = useRef();

  useEffect(() => {
    const systemSettingDark = window.matchMedia("(prefers-color-scheme: dark)");

    function toggleDarkMode(isDarkMode) {
      if (isDarkMode) {
        document.documentElement.classList.add("dark-mode");
        localStorage.setItem("dark-mode", "enabled");
      } else {
        document.documentElement.classList.remove("dark-mode");
        localStorage.setItem("dark-mode", "disabled");
      }
      setLogoBasedOnMode();
    }

    function setLogoBasedOnMode() {
      const myLogo = document.documentElement.getElementById("rowan-logo");
      if (!myLogo) return;

      if (document.documentElement.classList.contains('dark-mode')) {
        myLogo.src = '/images/Rowan-Morrison-logo-01.png'; 
      } else {
        myLogo.src = '/images/Rowan-Morrison-logo-02.png'; 
      }
    }

    toggleDarkMode(systemSettingDark.matches || localStorage.getItem("dark-mode") === "enabled");

    systemSettingDark.addEventListener("change", (event) => {
      toggleDarkMode(event.matches);
    });

    const el = toggleRef.current;
    if (el) {
      el.addEventListener("click", (e) => {
        e.preventDefault();
        const isDark = !document.documentElement.classList.contains("dark-mode");
        toggleDarkMode(isDark);
      });
    }

    return () => {
      if (el) el.removeEventListener("click", toggleDarkMode);
    };
  }, []);

  return (
    <footer id="footer">
      <p>&copy; 2025 Rowan Morrison</p>
      <li className="circle-container">
        <div id="mode-toggle">
          <motion.span
            ref={toggleRef}
            className="mode-toggle-button"
            id="light-icon"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            transition={{
              type: "tween",
              duration: 0.2,
              ease: "easeOut"
            }}
          />
        </div>
      </li>
    </footer>
  );
}

export default Footer;
