import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './WorkDetail.css';

/*function replaceWithCurlyQuotes(text) {
  const modifiedText = text
    .replace(/'([^']+)'/g, '‘$1’')
    .replace(/"([^"]+)"/g, '“$1”'); 

  console.log(modifiedText);
  return modifiedText;
}*/

const workDetails = {
  'Lucky-4': {
    title: 'Lucky Icons',
    description: replaceWithCurlyQuotes("Designed 24 Lucky icons for Carolina Bucci's Lucky collection."),
    image: '/images/Lucky-4.jpg',
  },
  'Downtown': {
    title: 'Downtown Ring',
    description: replaceWithCurlyQuotes("A ring inspired by downtown architecture."),
    image: '/images/Lucky-4.jpg',
  },
  'Memory-Pendants-8': {
    title: 'Memory Pendants',
    description: replaceWithCurlyQuotes("Drawing pendants to forever capture clients’ memories in 18k gold and precious stones."),
    caption: '© 2025 Carolina Bucci Firenze. All rights reserved.',
    image: [ '/images/Memory-Pendants-1.jpg',
      '/images/Memory-Pendants-2.jpg',
      '/images/Memory-Pendants-3.jpg',
      '/images/Memory-Pendants-4.jpg',
      '/images/Memory-Pendants-5.jpg',
      '/images/Memory-Pendants-6.jpg',
      '/images/Memory-Pendants-7.jpg',
      '/images/Memory-Pendants-8.jpg', 
      '/images/Memory-Pendants-9.jpg', 
      '/images/Memory-Pendants-10.jpg',]
  },
  'Central-23-2': {
    title: 'Central23',
    description: replaceWithCurlyQuotes("A pendant inspired by memories."),
    image: ['/images/Central-23-1.jpg', '/images/Central-23-2.jpg', '/images/Central-23-3.jpg',]
  },
};

function WorkDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const work = workDetails[id];

  if (!work) {return <div>Work not found.</div>;}

  return (
    <>
    <div className="work-detail">
    <motion.section
      className="work-info"
      initial={{ opacity: 0, x: -30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.3 }}
  >
        <button onClick={() => navigate(-1)} className="close-btn">
          <span class="material-symbols-outlined">
arrow_back
</span></button>
        <h1>{work.title}</h1>
        <p>{work.description}</p>
        <figcaption>{work.caption}</figcaption>
    </motion.section>

    <motion.section 
    className="work-image" 
    layoutId={`work-image-${id}-container`}>
      {Array.isArray(work.image) ? (
        work.image.map((img, index) => (
          <motion.img
          key={index}
          layoutId={`work-image-${id}-${index}`}
          src={img}
          alt={work.title}
          className="work-image-img"
        />
        ))
      ) : (
  <motion.img
    layoutId={`work-image-${id}`}
    src={work.image}
    alt={work.title}
  />
)}
</motion.section>
    </div>
    </>
  );
}

export default WorkDetail;
