import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import './WorkDetail.css';

const workDetails = {
  'Lucky-4': {
    title: 'Lucky Icons',
    description: "Designed 24 Lucky icons for Carolina Bucci's Lucky collection.",
    image: '/images/Lucky-4.jpg',
  },
  'Downtown': {
    title: 'Downtown Ring',
    description: 'A ring inspired by downtown architecture.',
    image: '/images/Lucky-4.jpg',
  },
  'memory-pendant': {
    title: 'Memory Pendant',
    description: 'A pendant inspired by memories.',
    image: '/images/memory.jpg',
  },
};

function WorkDetail() {
  const { id } = useParams();
  const work = workDetails[id];
  const [loaded, setLoaded] = useState(false);

useEffect(() => {
  setLoaded(true);
}, []);

  if (!work) {return <div>Work not found.</div>;}

  return (
    <>
    <div className={`work-detail ${loaded ? 'loaded' : ''}`}>
    <motion.section 
      className="work-info"
      initial={{ opacity: 0, x: -30 }}
  animate={{ opacity: 1, x: 0 }}
  transition={{ delay: 0.3 }}
  >
        <h1>{work.title}</h1>
        <p>{work.description}</p>
    </motion.section>
    <section className="work-image">
      <motion.img
        layoutId={`work-image-${id}`}
        src={work.image}
        alt={work.title}
        />
    </section>
    </div>
    </>
  );
}

export default WorkDetail;
