import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Home.css';

const workItems = [
  { id: 'CB-01', image: '/images/CB-01.jpg' },
  { id: 'Downtown-rings-tool', image: '/images/Downtown-rings-tool.mp4' },
  { id: 'collage', image: '/images/collage.jpeg' },
  { id: 'Lucky-4', image: '/images/Lucky-4.jpg' },
  { id: 'Central-23-2', image: '/images/Central-23-2.jpg' },
  { id: 'crocos-01', image: '/images/crocos-01.jpg' },
  { id: 'Archive', image: '/images/artists-01.jpeg' },
  { id: 'Memory-Pendants-8', image: '/images/Memory-Pendants-8.jpg' },
  { id: 'cat-oracle-1', image: '/images/cat-oracle-1.jpg' },
];

function Home() {
  return (
    <div className="grid">
      {workItems.map(item => (
        <motion.div 
        key={item.id} 
        className="grid-item"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, ease: "easeOut" }}
        >
          <Link to={`/work/${item.id}`}>
          <motion.div 
          layoutId={`work-image-${item.id}-container`} 
          className="grid-image-wrapper">
      {item.image.endsWith('.mp4') ? (
    <motion.video
      layoutId={`work-image-${item.id}`}
      src={item.image}
      alt={item.title}
      controls
      autoPlay
      muted
      loop
      className="grid-video"
    />
  ) : (
    <motion.img
      layoutId={`work-image-${item.id}`}
      src={item.image}
      alt={item.title}
      className="grid-image"
    />
  )}
  </motion.div>
          </Link>
        </motion.div>
      ))}
    </div>
  );
}

export default Home;
