import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './WorkDetail.css';

const workDetails = {
  'cat-oracle-1': {
    title: 'Cat Oracle',
    description: "A mini site to find out some facts about cats.",
    image: '/images/cat-oracle-1.jpg',
    link: 'https://catoracle.rowanmorrisons.com',
  },
  'Lucky-4': {
    title: 'Lucky Icons',
    description: "Designed 24 Lucky icons for Carolina Bucci’s Lucky collection.",
    image: [ '/images/Lucky-1.jpg',
      '/images/Lucky-2.jpg',
      '/images/Lucky-3.jpg',
      '/images/Lucky-4.jpg',
     ]
  },
  'Downtown-rings-tool': {
    title: 'Downtown Ring Interactive Tool',
    description: "A built-in PDP tool for clients to play with the many possibilities of Carolina's Downtown rings.\nThe Downtown ring features one of the 12 Downtown precious stones, and is crafted from 18k yellow, white or pink gold in Carolina Bucci’s signature Florentine Finish.",
    image: '/images/Downtown-rings-tool.mp4',
  },
  'Memory-Pendants-8': {
    title: 'Memory Pendants',
    description: "Drawing pendants to forever capture clients’ memories in 18k gold and precious stones.",
    caption: '© 2025 Carolina Bucci Firenze. All rights reserved.',
    image: [ '/images/Memory-Pendants-1.jpg',
      '/images/Memory-Pendants-2.jpg',
      '/images/Memory-Pendants-3.jpg',
      '/images/Memory-Pendants-4.jpg',
      '/images/Memory-Pendants-5.jpg',
      '/images/Memory-Pendants-6.jpg',
      '/images/Memory-Pendants-7.jpg',
      '/images/Memory-Pendants-8.jpg', 
      '/images/Memory-Pendants-9.jpg', 
      '/images/Memory-Pendants-10.jpg',]
  },
  'Central-23-2': {
    title: 'Central23',
    description: "Wrapping paper designs for award-winning packaging brand Central23.",
    image: ['/images/Central-23-1.jpg', 
      '/images/Central-23-2.jpg', 
      '/images/Central-23-3.jpg',]
  },
  'Archive': {
    title: 'Archival work',
    description: "A selection of analogue and digital drawings.",
    image: ['/images/Archive-01.jpeg', 
      '/images/Archive-02.jpg', 
      '/images/Archive-03.jpeg',
    '/images/Archive-04.jpeg',
  '/images/Archive-05.gif',
'/images/Archive-06.gif',]
  },
};

function WorkDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const work = workDetails[id];


  const [previewIndex, setPreviewIndex] = useState(null);

  const handlePrev = () => {
    if (previewIndex > 0) setPreviewIndex(previewIndex - 1);
  };

  const handleNext = () => {
    if (work.image && previewIndex < work.image.length - 1) {
      setPreviewIndex(previewIndex + 1);
    }
  };

  const closePreview = () => setPreviewIndex(null);

  if (!work) {return <div>Work not found.</div>;}

  return (
    <>
    <div className="work-detail">
    <motion.section
      className="work-info"
      initial={{ opacity: 0, x: -30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.3 }}
  >
        <button onClick={() => navigate(-1)} className="close-btn">
          <span className="material-symbols-outlined">
arrow_back
</span></button>
        <h1>{work.title}</h1>
        <p>{work.description}</p>
        {work.link && (
  <a href={work.link} 
  target="_blank" 
  rel="noopener noreferrer" 
  className="external-link-button">
    Visit Project
  </a>
)}
        <figcaption>{work.caption}</figcaption>
    </motion.section>

    <motion.section 
    className="work-image" 
    layoutId={`work-image-${id}-container`}>
      {Array.isArray(work.image) ? (
        work.image.map((img, index) => (
          <motion.img
          key={index}
          layoutId={`work-image-${id}-${index}`}
          src={img}
          alt={work.title}
          className="work-image-img"
        />
        ))
      ) : (
  <motion.img
    layoutId={`work-image-${id}`}
    src={work.image}
    alt={work.title}
  />
)}
</motion.section>
    </div>
    </>
  );
}

export default WorkDetail;
