import React from 'react';
import './Footer.css';

function Footer() {
  return (
    <footer id="footer">
      <p>&copy; Rowan Morrison 2025</p>
      <li class="circle-container">
									<a id="mode-toggle" href="#">
									<span class="material-symbols-outlined" id="light-icon">
										</span>
								</a>
							</li>
    </footer>
  );
}

export default Footer;

// Dark Mode Setup
const systemSettingDark = window.matchMedia("(prefers-color-scheme: dark)");

function toggleDarkMode(isDarkMode) {
    if (isDarkMode) {
        document.body.classList.add("dark-mode");
        localStorage.setItem("dark-mode", "enabled"); // Store dark mode preference
    } else {
        document.body.classList.remove("dark-mode");
        localStorage.setItem("dark-mode", "disabled"); // Store light mode preference
    }
	setLogoBasedOnMode();
}

	function setLogoBasedOnMode() {
		const myLogo = document.getElementById("myLogo");
		if (document.body.classList.contains('dark-mode')) {
		  myLogo.src = './images/Rowan-Morrison-logo-01.png';  // Dark mode logo
		} else {
		  myLogo.src = './images/Rowan-Morrison-logo-02.png'; // Light mode logo
		}
	  }

toggleDarkMode(systemSettingDark.matches || localStorage.getItem("dark-mode") === "enabled");

systemSettingDark.addEventListener("change", (event) => {
    toggleDarkMode(event.matches);
});

document.addEventListener('DOMContentLoaded', function () {
    const modeToggle = document.getElementById("mode-toggle");

    if (modeToggle) {
        modeToggle.addEventListener('click', function (e) {
            e.preventDefault();
            const isDarkMode = !document.body.classList.contains('dark-mode');
            toggleDarkMode(isDarkMode);
        });
    }
});
